import pyncm,pyncm.apis
from pprint import pprint
def verify(response):
    try:
        assert type(response)==dict, "响应无效，检查加密实现！"
        assert response.get('code',0) == 200, "请求被拒，检查参数配置！"        
        print('[PASS]',end=' -- ')
    except AssertionError as e:
        print('FAIL : %s' % e,end=' -- ')
    pprint(response)
print('* Login')
# verify(pyncm.apis.login.LoginViaCellphone('3156678705','1q2w3e4R',ctcode='1'))
pyncm.SetCurrentSession(pyncm.LoadSessionFromString(''))
print('* SetLike (Set) (EAPI)')
verify(pyncm.apis.track.SetLikeTrack(31140560,like=True))
print('* SetLike (Revert) (EAPI)')
verify(pyncm.apis.track.SetLikeTrack(31140560,like=False))
print('* Create playlist (EAPI)')
verify(playlist:=pyncm.apis.playlist.SetCreatePlaylist('Playlist'))
print('* Add track to playlist (WEAPI)')
verify(pyncm.apis.playlist.SetManipulatePlaylistTracks(31140560,playlist.get('id',0)))
print('* Delete playlist (EAPI)')
verify(pyncm.apis.playlist.SetRemovePlaylist(playlist.get('id',0)))
print('* Top playlists (LinuxAPI)')
verify(pyncm.apis.playlist.GetTopPlaylists())